import { useLocation, useNavigate } from "react-router-dom";
import { AiOutlineSearch } from "react-icons/ai";

export default function Header() {
    const location = useLocation();
    const navigate = useNavigate();

    function pathMatchRoute(route) {
        return route === location.pathname;
    }

    return (
        <div className="bg-white border-b shadow-sm sticky top-0 z-50">
            <header className="grid grid-cols-12 items-center max-w-6xl mx-auto px-4 h-14">
                {/* Logo Section */}
                <div className="col-span-2 flex justify-start">
                    <img
                        src="./images/logo.png"
                        alt="logo"
                        className="h-8 cursor-pointer"
                        onClick={() => navigate("/")}
                    />
                </div>

                {/* Search Bar Section */}
                <div className="col-span-4 flex justify-center ml-8">
                    <form className="flex items-center border-2 border-gray-300 rounded-full shadow-md w-full">
                        <input
                            type="text"
                            placeholder="Search..."
                            className="bg-transparent px-4 py-1.5 w-full text-sm text-gray-700 focus:outline-none rounded-l-full"
                        />
                        <button
                            type="submit"
                            className="flex items-center justify-center px-3 py-1.5 bg-white text-gray-700 rounded-r-full border-l border-gray-300"
                        >
                            <AiOutlineSearch size={16} />
                        </button>
                    </form>
                </div>

                {/* Navigation Links Section */}
                <div className="col-span-3 flex justify-start ml-10">
                    <ul className="flex space-x-5">
                        <li
                            className={`cursor-pointer py-3 text-sm font-semibold text-gray-400 border-b-[3px] border-b-transparent
                                ${pathMatchRoute("/") && "text-gray-950 border-b-orange-400"}`}
                            onClick={() => navigate("/")}
                        >
                            Home
                        </li>
                        <li
                            className={`cursor-pointer py-3 text-sm font-semibold text-gray-400 border-b-[3px] border-b-transparent
                                ${pathMatchRoute("/about") && "border-b-orange-400 text-gray-950"}`}
                            onClick={() => navigate("/about")}
                        >
                            About
                        </li>
                        <li
                            className={`cursor-pointer py-3 text-sm font-semibold text-gray-400 border-b-[3px] border-b-transparent
                                ${pathMatchRoute("/projects") && "border-b-orange-400 text-gray-950"}`}
                            onClick={() => navigate("/projects")}
                        >
                            Projects
                        </li>
                    </ul>
                </div>

                {/* Auth Buttons Section */}
                <div className="col-span-3 flex justify-end space-x-4 mr-3">
                    <button
                        className="px-5 py-1.5 text-sm font-semibold text-gray-700 border border-gray-300 rounded-full shadow-md hover:shadow-lg"
                        onClick={() => navigate("/sign-up")}
                    >
                        Sign Up
                    </button>
                </div>
            </header>
        </div>
    );
}
